game_updates.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  width: 450px;
  height: 600px;
  overflow: hidden;
}

.container {
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;
}

.header {
  text-align: center;
  color: white;
  width: 100%;
}

.header h1 {
  font-size: 28px;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
}

.score-board {
  display: flex;
  justify-content: space-around;
  font-size: 16px;
  font-weight: bold;
  background: rgba(255,255,255,0.3);
  padding: 10px;
  border-radius: 10px;
}

.game-info {
  background: rgba(255,255,255,0.9);
  padding: 8px 15px;
  border-radius: 5px;
  font-size: 12px;
  color: #333;
  width: 100%;
  text-align: center;
}

#gameCanvas {
  border: 4px solid white;
  border-radius: 10px;
  background: #1a1a1a;
  box-shadow: 0 8px 16px rgba(0,0,0,0.3);
}

.controls {
  display: flex;
  gap: 10px;
  width: 100%;
  justify-content: center;
}

.controls button {
  padding: 12px 24px;
  font-size: 16px;
  font-weight: bold;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 6px rgba(0,0,0,0.2);
}

#startBtn {
  background: #4CAF50;
  color: white;
}

#startBtn:hover {
  background: #45a049;
  transform: translateY(-2px);
}

#pauseBtn {
  background: #ff9800;
  color: white;
}

#pauseBtn:hover:not(:disabled) {
  background: #e68900;
  transform: translateY(-2px);
}

#updateBtn {
  background: #2196F3;
  color: white;
}

#updateBtn:hover {
  background: #0b7dda;
  transform: translateY(-2px);
}

button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.instructions {
  color: white;
  text-align: center;
  font-size: 14px;
  background: rgba(255,255,255,0.3);
  padding: 10px;
  border-radius: 8px;
  width: 100%;
}
